import { Bar, Chart } from "react-chartjs-2";
import { CategoryScale, Chart as ChartJS, LinearScale, BarElement } from "chart.js";

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
);




function Home() {

    return (
        <div className="Main">
            <div className="Header">
                <h1 className="Title">Co2Ampel</h1>
            </div>


            <MainPart />
            <GraphArea />
            <TrafficLight />
        </div>


    )

}
export default Home;



function MainPart() {
    return (
        <div className="biggerPart">
            <button id="redbutton">Rote Farbe anstellen</button>
            <button id="yellowbutton">Gelbe Farbe anstellen</button>
            <button id="greenbutton">Gr√ºne Farbe anstellen</button>
        </div>
    )
}



function TrafficLight() {
    return (
        <div className="trafficLight">
            <div className="square">
                <div className="circle"></div>
                <div className="circle0"></div>
                <div className="circle1"></div>
            </div>
        </div>
    )
}





function GraphArea() {
    return (
        <div className="graphArea">
            <BarChart />
        </div>
    )
}

function BarChart() {

    const options = {
        responsive: false,
        maintainAspectRatio: false,
        plugins: {
            legend: {
                position: 'top' as const,
            },
            title: {
                display: true,
                text: 'Chart.js Bar Chart',
            },
        },
    }

    const labels = ["1", "2", "3", "4", "5", "6", "7", "8"]
    const data = {
        labels: labels,
        datasets: [{
            label: 'My First Dataset',
            data: [65, 59, 80, 81, 56, 55, 40, 49, 129, 193, 12, 3, 123,],
            backgroundColor: [
                'black'
            ],
        }]
    };

    return (
        <Bar data={data} options={options} />
    )

}
